            carousalImages.add(getProductCarousalImage("airpods_gen3", 2, "/IMAGE_PATH/airpods_gen3_image2.jpg"))
            carousalImages.add(getProductCarousalImage("airpods_gen3", 1, "/IMAGE_PATH/airpods_gen3_image1.jpg"))
            carousalImages.add(getProductCarousalImage("airpods_gen3", 3, "/IMAGE_PATH/airpods_gen3_image3.jpg"))
        }

        if (groupName.equalsIgnoreCase("iphone11-product_red")) {
            carousalImages.add(getProductCarousalImage("iphone11-product_red", 1, "/IMAGE_PATH/iphone11Red_image1.jpg"))
            carousalImages.add(getProductCarousalImage("iphone11-product_red", 2, "/IMAGE_PATH/iphone11Red_image2.jpg"))
        }

        if (groupName.equalsIgnoreCase("iphone11-green")) {
            carousalImages.add(getProductCarousalImage("iphone11-green", 1, "/IMAGE_PATH/iphone11Green_image1.jpg"))
            carousalImages.add(getProductCarousalImage("iphone11-green", 2, "/IMAGE_PATH/iphone11Green_image2.jpg"))
        }

        if (groupName.equalsIgnoreCase("watch7-nike-41mm-alum-starlight-pureplatinum")) {
            carousalImages.add(getProductCarousalImage("watch7-nike-41mm-alum-starlight-pureplatinum", 1, "/IMAGE_PATH/watchNike7_41mm_image1.jpg"))
