    void readSheetAndGenerateScript() {
        String line
        try {
            // S.No.,	order,	group_name,	image_url
            BufferedReader br = new BufferedReader(new FileReader("C:\\sheet\\carousel\\read.csv"))
            List<ProductCarouselImageDB> tempList = new ArrayList<>()
            while ((line = br.readLine()) != null) {
                String[] strArr = line.split(",")
                tempList.add(new ProductCarouselImageDB(strArr[1], strArr[2], strArr[3]))
            }

            String groupName = ""
            for (ProductCarouselImageDB record : tempList) {
                if (groupName != record.getGroupName()) {
                    groupName = record.getGroupName()
                    LOGGER.warn("SELECT @group_name = '" + record.getGroupName() + "'")
                }
                LOGGER.warn("SELECT @image_url = '" + record.getImageUrl() + "'")
                LOGGER.warn("SELECT @image_order = " + record.getImageOrder())
                LOGGER.warn("EXEC dbo.usp_product_carousal_image @group_name, @image_url, @image_order, @active_ind")
                LOGGER.warn("")
            }

        } catch (IOException e) {
            LOGGER.error("Exception" + e)
        }
    }

