    void printDetails(String psid, String categoryName, List<Option> psOptions) {
        LOGGER.warn("-- PSID --> " + psid + " & Category --> " + categoryName);
        String categoryShortName = getShortName(categoryName)//apple-watch-nikeplus-series7 to watch7-nike

        //{"caseSize":"watch41mm","caseMaterial":"aluminum","caseFinish":"starlight","bandColor":"pureplatinumblack"};
        String optionString = convertToJson(categoryShortName, psOptions,
                Arrays.asList("caseSize", "caseMaterial", "caseFinish", "bandColor", "communication"))//Needed Options
        LOGGER.warn(" SELECT @options= '" + optionString +"';")
        LOGGER.warn(" EXEC  dbo.usp_product_options_config @locale, @category_name, @psid, @options, @group_name, @active_ind")
    }

    String convertToJson(String categoryShortName, List<Option> options, List<String> neededConfigs) {
        StringBuilder groupName = new StringBuilder(categoryShortName)
        Map<String, String> configOptionMap = new LinkedHashMap<>()
        for (String config : neededConfigs) {
            for (Option option : options) {
                if (config == option.getName()) {
                    configOptionMap.put(option.getName(), option.getKey())
                    groupName.append(getShortName(option.getKey()))
                    break
                }
            }
        }
        LOGGER.warn("SELECT @group_name= '" + groupName+"';")
        return new Gson().toJson(configOptionMap)
    }

    String getShortName(String name) {
        switch (name) {
            case "apple-watch-ultra": return "watch-ultra"
            case "apple-watch-se": return "watch-se"
            case "watch-apple-watch-series-7": return "watch7"
            case "watch-apple-watch-series-8": return "watch8"
            case "apple-watch-nikeplus-series7": return "watch7-nike"

            case "watch40mm": return "-40mm"
            case "watch41mm": return "-41mm"
            case "watch44mm": return "-44mm"
            case "watch45mm": return "-45mm"

            case "iPhone 14 Pro Max": return "-max"
            case "iPhone 14 Pro": return ""
            case "iPhone 14": return ""
            case "iPhone 14 Plus": return "-plus"

            case "aluminum": return "-alum"
            case "gps_cellular": return "-lte"
            case "anthracite": return "-anthr"
            case "pureplatinum": return "-pureplat"
            case "product_red": return "-red"
        }
        return "-" + name;
    }
